mapper.insert:
/* KanbanBoardMapper.insertTask */

    SELECT IFNULL(MAX(task_id)+1, 1)
      FROM kanban_tasks

INSERT INTO kanban_tasks (
            task_id
          , kanban_id
          , column_id
          , subject
          , content
          , start_date
          , end_date
          , sort
          , importance
          , create_date
          , modify_date
          , user_id
) VALUES (
            #{taskId}
          , #{kanbanId}
          , #{columnId}
          , #{subject}
          , #{content}
          , #{startDate}
          , #{endDate}
          , #{sort}
          , #{importance}
          , NOW()
          , NOW()
          , #{userId}
)

/* KanbanBoardMapper.insertTaskUsers */
INSERT INTO task_users (
            user_id
          , task_id
) VALUES (
            #{userId}
          , #{taskId}
)

mapper.update:
/* KanbanBoardMapper.updateTask */
UPDATE kanban_tasks
   SET subject = #{subject}
     , content = #{content}
     , start_date = #{startDate}
     , end_date = #{endDate}
     , importance = #{importance}
     , modify_date = NOW()
 WHERE task_id = #{taskId}
   AND kanban_id = #{kanbanId}
   AND column_id = #{columnId}

/* KanbanBoardMapper.deleteTask */
UPDATE kanban_tasks
   SET delete_date = NOW()
 WHERE task_id = #{taskId}
